body.script:
                    // Variables globales
                    let chartsData = ;
                    let drillDownData = ;
                    let currentChart = 'requests';
                    let charts = {};

                    // Initialisation au chargement de la page
                    document.addEventListener('DOMContentLoaded', function() {
                        initializeCharts();
                    });

                    // Initialiser tous les graphiques
                    function initializeCharts() {
                        try {
                            initMonthlyChart();
                            initStatusChart();
                        } catch (error) {
                            console.error('Erreur lors de l\'initialisation des graphiques:', error);
                        }
                    }

                    // Graphique d'évolution mensuelle
                    function initMonthlyChart() {
                        const ctx = document.getElementById('monthlyChart');
                        if (!ctx) return;

                        const data = chartsData.monthly_requests || [];

                        charts.monthly = new Chart(ctx, {
                            type: 'line',
                            data: {
                                labels: data.map(item => item.month_name || ''),
                                datasets: [{
                                    label: 'Demandes par mois',
                                    data: data.map(item => item.count || 0),
                                    borderColor: '#0d6efd',
                                    backgroundColor: 'rgba(13, 110, 253, 0.1)',
                                    tension: 0.4,
                                    fill: true
                                }]
                            },
                            options: {
                                responsive: true,
                                maintainAspectRatio: false,
                                plugins: {
                                    legend: {
                                        display: false
                                    }
                                },
                                scales: {
                                    y: {
                                        beginAtZero: true
                                    }
                                }
                            }
                        });
                    }

                    // Graphique de répartition par état
                    function initStatusChart() {
                        const ctx = document.getElementById('statusChart');
                        if (!ctx) return;

                        const data = chartsData.requests_by_status || [];

                        charts.status = new Chart(ctx, {
                            type: 'doughnut',
                            data: {
                                labels: data.map(item => item.status || ''),
                                datasets: [{
                                    data: data.map(item => item.count || 0),
                                    backgroundColor: [
                                        '#198754', // Répondues - vert
                                        '#0dcaf0', // En traitement - cyan
                                        '#ffc107', // Soumises - jaune
                                        '#fd7e14', // En validation - orange
                                        '#dc3545'  // Refusées - rouge
                                    ]
                                }]
                            },
                            options: {
                                responsive: true,
                                maintainAspectRatio: false,
                                plugins: {
                                    legend: {
                                        position: 'bottom'
                                    }
                                }
                            }
                        });
                    }

                    // Fonction de drill down
                    function drillDown(metric) {
                        showLoading();

                        // Simuler un délai de chargement
                        setTimeout(() => {
                            hideLoading();
                            showDrillDownSection(metric);
                        }, 500);
                    }

                    // Afficher la section drill down
                    function showDrillDownSection(metric) {
                        document.getElementById('main-dashboard').style.display = 'none';
                        document.getElementById('drill-down-section').style.display = 'block';

                        // Mettre à jour le breadcrumb
                        const titles = {
                            'total_requests': 'Détails des Demandes',
                            'processing_rate': 'Taux de Traitement',
                            'avg_processing_days': 'Délais de Traitement',
                            'on_time_rate': 'Respect des Délais'
                        };

                        document.getElementById('current-section').textContent = titles[metric] || 'Détails';
                        document.getElementById('drill-down-title').textContent = titles[metric] || 'Détails';

                        // Charger le contenu spécifique
                        loadDrillDownContent(metric);
                    }

                    // Charger le contenu du drill down
                    function loadDrillDownContent(metric) {
                        const content = document.getElementById('drill-down-content');

                        let html = '';

                        switch(metric) {
                            case 'total_requests':
                                html = generateRequestsDetails();
                                break;
                            case 'processing_rate':
                                html = generateProcessingRateDetails();
                                break;
                            case 'avg_processing_days':
                                html = generateProcessingTimeDetails();
                                break;
                            case 'on_time_rate':
                                html = generateOnTimeDetails();
                                break;
                            default:
                                html = '
Détails non disponibles pour cette métrique.';
                        }

                        content.innerHTML = html;
                    }

                    // Générer les détails des demandes
                    function generateRequestsDetails() {
                        const data = chartsData.requests_by_status || [];
                        let html = '
Répartition détaillée des demandes par état (données réelles)';
                        html += '
';

                        data.forEach(item => {
                            html += `







${item.count || 0}

${item.status || ''}
                                            Backend réel



                            `;
                        });

                        html += '';
                        return html;
                    }

                    // Générer les détails du taux de traitement
                    function generateProcessingRateDetails() {
                        return `

Évolution du taux de traitement (données réelles)

Le taux de traitement est calculé en temps réel à partir des données de la base SAMA CONAI.


                                Calcul : (Demandes traitées / Total des demandes) × 100

                        `;
                    }

                    // Générer les détails des délais
                    function generateProcessingTimeDetails() {
                        return `

Analyse des délais de traitement (données réelles)

Les délais sont calculés automatiquement à partir des dates réelles de traitement.


                                Méthode : Différence entre date de réponse et date de demande pour chaque dossier traité

                        `;
                    }

                    // Générer les détails du respect des délais
                    function generateOnTimeDetails() {
                        return `

Respect du délai légal de 30 jours (données réelles)

Pourcentage calculé en temps réel des demandes traitées dans le délai légal.


                                Calcul : Demandes traitées en ≤ 30 jours / Total des demandes traitées

                        `;
                    }

                    // Retour au dashboard principal
                    function showMainDashboard() {
                        document.getElementById('drill-down-section').style.display = 'none';
                        document.getElementById('main-dashboard').style.display = 'block';
                        document.getElementById('current-section').textContent = 'Tableau de Bord';
                    }

                    // Changer de graphique
                    function switchChart(type) {
                        currentChart = type;

                        // Mettre à jour les boutons
                        document.querySelectorAll('.btn-group .btn').forEach(btn => {
                            btn.classList.remove('active');
                        });
                        event.target.classList.add('active');

                        // Mettre à jour le graphique
                        updateMonthlyChart(type);
                    }

                    // Mettre à jour le graphique mensuel
                    function updateMonthlyChart(type) {
                        if (!charts.monthly) return;

                        const data = type === 'requests' ? 
                            chartsData.monthly_requests || [] : 
                            chartsData.monthly_alerts || [];

                        charts.monthly.data.labels = data.map(item => item.month_name || '');
                        charts.monthly.data.datasets[0].data = data.map(item => item.count || 0);
                        charts.monthly.data.datasets[0].label = type === 'requests' ? 'Demandes par mois' : 'Signalements par mois';
                        charts.monthly.data.datasets[0].borderColor = type === 'requests' ? '#0d6efd' : '#ffc107';
                        charts.monthly.data.datasets[0].backgroundColor = type === 'requests' ? 'rgba(13, 110, 253, 0.1)' : 'rgba(255, 193, 7, 0.1)';

                        charts.monthly.update();
                    }

                    // Afficher le spinner de chargement
                    function showLoading() {
                        document.getElementById('loading-spinner').style.display = 'block';
                    }

                    // Masquer le spinner de chargement
                    function hideLoading() {
                        document.getElementById('loading-spinner').style.display = 'none';
                    }

                    // Actualiser le dashboard
                    function refreshDashboard() {
                        showLoading();

                        // Appel AJAX pour récupérer les nouvelles données
                        fetch('/transparence-dashboard/api/data', {
                            method: 'POST',
                            headers: {
                                'Content-Type': 'application/json',
                            },
                            body: JSON.stringify({})
                        })
                        .then(response => response.json())
                        .then(data => {
                            if (data.success) {
                                // Mettre à jour les statistiques
                                updateStatistics(data.stats);
                                // Mettre à jour les graphiques
                                updateCharts(data.charts_data);
                            }
                            hideLoading();
                        })
                        .catch(error => {
                            console.error('Erreur lors de l\'actualisation:', error);
                            hideLoading();
                            // Fallback: recharger la page
                            location.reload();
                        });
                    }

                    // Mettre à jour les statistiques affichées
                    function updateStatistics(stats) {
                        document.getElementById('total-requests-value').textContent = stats.total_requests || 0;
                        document.getElementById('processing-rate-value').textContent = stats.processing_rate || 0;
                        document.getElementById('avg-days-value').textContent = stats.avg_processing_days || 0;
                        document.getElementById('on-time-rate-value').textContent = stats.on_time_rate || 0;
                        document.getElementById('recent-requests').textContent = stats.recent_requests || 0;
                        document.getElementById('processed-requests').textContent = stats.processed_requests || 0;
                        document.getElementById('total-requests-small').textContent = stats.total_requests || 0;
                    }

                    // Mettre à jour les graphiques
                    function updateCharts(newChartsData) {
                        chartsData = newChartsData;

                        // Réinitialiser les graphiques avec les nouvelles données
                        if (charts.monthly) {
                            charts.monthly.destroy();
                        }
                        if (charts.status) {
                            charts.status.destroy();
                        }

                        initializeCharts();
                    }
