#!/bin/bash\n\n# ========================================= #\n# SAMA CONAI - ARRÊT VERSION ODOO RÉELLE   #\n# ========================================= #\n\necho \"🇸🇳 SAMA CONAI - Arrêt Version Odoo Réelle\"\necho \"===========================================\"\necho \"\"\n\n# Couleurs\nGREEN='\\033[0;32m'\nBLUE='\\033[0;34m'\nYELLOW='\\033[1;33m'\nRED='\\033[0;31m'\nNC='\\033[0m'\n\nprint_step() {\n    echo -e \"${BLUE}➤${NC} $1\"\n}\n\nprint_success() {\n    echo -e \"${GREEN}✅${NC} $1\"\n}\n\nprint_warning() {\n    echo -e \"${YELLOW}⚠️${NC} $1\"\n}\n\n# Variables\nPID_FILE=\"pids/sama_conai_odoo_real.pid\"\nPORT=3008\n\n# Arrêt par PID\nprint_step \"Arrêt du serveur Odoo réel...\"\n\nif [ -f \"$PID_FILE\" ]; then\n    PID=$(cat \"$PID_FILE\")\n    if ps -p $PID > /dev/null 2>&1; then\n        kill $PID\n        sleep 2\n        \n        # Vérification\n        if ps -p $PID > /dev/null 2>&1; then\n            print_warning \"Arrêt forcé...\"\n            kill -9 $PID 2>/dev/null\n        fi\n        \n        print_success \"Serveur arrêté (PID: $PID)\"\n    else\n        print_warning \"Processus déjà arrêté\"\n    fi\n    \n    rm -f \"$PID_FILE\"\nelse\n    print_warning \"Fichier PID non trouvé\"\nfi\n\n# Nettoyage par nom de processus\nprint_step \"Nettoyage des processus...\"\npkill -f \"server_odoo_real.js\" 2>/dev/null && print_success \"Processus nettoyés\" || print_warning \"Aucun processus à nettoyer\"\n\n# Vérification du port\nprint_step \"Vérification du port $PORT...\"\nif ! curl -s --connect-timeout 2 \"http://localhost:$PORT\" > /dev/null 2>&1; then\n    print_success \"Port $PORT libéré\"\nelse\n    print_warning \"Port $PORT encore occupé\"\nfi\n\necho \"\"\nprint_success \"SAMA CONAI Version Odoo Réelle arrêtée\"\necho \"\"\necho \"📊 Version simulée toujours disponible :\"\necho \"   http://localhost:3007/enriched\"\necho \"\"\necho \"🔄 Pour relancer la version Odoo réelle :\"\necho \"   ./launch_odoo_real_sama_conai.sh\"\necho \"\"\necho \"===========================================\"\necho \"🇸🇳 SAMA CONAI - Arrêt Terminé\"\necho \"===========================================\"